$("#theInfo").find("li").on("click", function(){
    if(!$(this).prop('class').includes('selected')){
        $("#theInfo").find('li').removeClass('selected');
        var thisId = $(this).prop("id");
        $("#songTitle").html("<b><i>"+songs[thisId].title+"</b></i>");
        $("#lyrText").hide().html(songs[thisId].song).fadeIn("fast");
        $(this).siblings().removeClass("selected");
        $(this).addClass("selected");
    }   
});

